import { Card, CardContent } from "@/components/ui/card";
import { TrendingUp, TrendingDown, ChevronRight } from "lucide-react";
import type { Stock } from "@/types/stock";

interface StockCardProps {
  stock: Stock;
  onClick?: (stock: Stock) => void;
  className?: string;
}

export function StockCard({ stock, onClick, className }: StockCardProps) {
  const isPositive = stock.change >= 0;

  const formatVolume = (volume: number): string => {
    if (volume >= 1000000) {
      return `${(volume / 1000000).toFixed(1)}M`;
    } else if (volume >= 1000) {
      return `${(volume / 1000).toFixed(1)}K`;
    }
    return volume.toString();
  };

  return (
    <Card className={`hover:shadow-md transition-shadow cursor-pointer ${className}`} onClick={() => onClick?.(stock)}>
      <CardContent className="p-6">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-4 lg:space-y-0">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 rounded-lg bg-primary/10 flex items-center justify-center">
              <span className="text-primary font-bold text-lg" data-testid={`stock-symbol-${stock.symbol}`}>
                {stock.symbol}
              </span>
            </div>
            <div className="flex-1">
              <h3 className="font-semibold text-lg" data-testid={`stock-name-${stock.symbol}`}>
                {stock.name}
              </h3>
              <p className="text-muted-foreground text-sm" data-testid={`stock-sector-${stock.symbol}`}>
                {stock.sector || 'Unknown'}
              </p>
            </div>
          </div>

          <div className="flex flex-col sm:flex-row items-start sm:items-center space-y-2 sm:space-y-0 sm:space-x-8">
            <div className="text-right">
              <div className="text-2xl font-bold" data-testid={`stock-price-${stock.symbol}`}>
                ${stock.currentPrice.toFixed(2)}
              </div>
              <div className="text-sm text-muted-foreground">Current Price</div>
            </div>

            <div className="text-right">
              <div className="flex items-center space-x-1">
                {isPositive ? (
                  <TrendingUp className="text-green-600 dark:text-green-400 text-xs" />
                ) : (
                  <TrendingDown className="text-red-600 dark:text-red-400 text-xs" />
                )}
                <span 
                  className={`font-semibold ${
                    isPositive 
                      ? 'text-green-600 dark:text-green-400' 
                      : 'text-red-600 dark:text-red-400'
                  }`}
                  data-testid={`stock-change-${stock.symbol}`}
                >
                  {isPositive ? '+' : ''}${stock.change.toFixed(2)}
                </span>
              </div>
              <div 
                className={`text-sm ${
                  isPositive 
                    ? 'text-green-600 dark:text-green-400' 
                    : 'text-red-600 dark:text-red-400'
                }`}
                data-testid={`stock-change-percent-${stock.symbol}`}
              >
                {isPositive ? '+' : ''}{stock.changePercent.toFixed(2)}%
              </div>
            </div>

            <div className="text-right">
              <div className="font-semibold" data-testid={`stock-volume-${stock.symbol}`}>
                {formatVolume(stock.volume)}
              </div>
              <div className="text-sm text-muted-foreground">Volume</div>
            </div>

            <div className="hidden lg:flex items-center text-muted-foreground">
              <span className="text-sm mr-2">View Details</span>
              <ChevronRight className="w-4 h-4" />
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/stock-chart.tsx.content:
import { useEffect, useRef, useState } from 'react';

interface StockChartProps {
  symbol: string;
  currentPrice: number;
  width?: number;
  height?: number;
}

declare global {
  interface Window {
    TradingView: any;
  }
}

export function StockChart({ symbol, currentPrice, width = 700, height = 400 }: StockChartProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const widgetRef = useRef<any>(null);
  const [isLoaded, setIsLoaded] = useState(false);

  // Format symbol for TradingView (most US stocks are on NASDAQ)
  const formatSymbolForTradingView = (sym: string) => {
    // Common exchange mappings
    const exchangeMap: { [key: string]: string } = {
      'AAPL': 'NASDAQ:AAPL',
      'MSFT': 'NASDAQ:MSFT',
      'GOOGL': 'NASDAQ:GOOGL',
      'GOOG': 'NASDAQ:GOOG',
      'AMZN': 'NASDAQ:AMZN',
      'TSLA': 'NASDAQ:TSLA',
      'META': 'NASDAQ:META',
      'NVDA': 'NASDAQ:NVDA',
      'NFLX': 'NASDAQ:NFLX',
      'ORCL': 'NASDAQ:ORCL',
      'ADBE': 'NASDAQ:ADBE',
      'CRM': 'NYSE:CRM',
      'PYPL': 'NASDAQ:PYPL',
      'INTC': 'NASDAQ:INTC',
      'CSCO': 'NASDAQ:CSCO',
      'PEP': 'NASDAQ:PEP',
      'KO': 'NYSE:KO',
      'DIS': 'NYSE:DIS',
      'VZ': 'NYSE:VZ',
      'NKE': 'NYSE:NKE',
      'WMT': 'NYSE:WMT',
      'HD': 'NYSE:HD',
      'BAC': 'NYSE:BAC',
      'JPM': 'NYSE:JPM',
      'V': 'NYSE:V',
      'MA': 'NYSE:MA',
      'JNJ': 'NYSE:JNJ',
      'PG': 'NYSE:PG',
      'UNH': 'NYSE:UNH',
      'ABBV': 'NYSE:ABBV',
      'F': 'NYSE:F',
      'GE': 'NYSE:GE',
      'T': 'NYSE:T',
      'XOM': 'NYSE:XOM',
      'CVX': 'NYSE:CVX'
    };

    // Return mapped symbol or default to NASDAQ
    return exchangeMap[sym.toUpperCase()] || `NASDAQ:${sym.toUpperCase()}`;
  };

  useEffect(() => {
    if (!containerRef.current) return;

    const tradingViewSymbol = formatSymbolForTradingView(symbol);

    // Clear previous widget
    if (containerRef.current) {
      containerRef.current.innerHTML = '';
    }

    // Create widget container
    const widgetContainer = document.createElement('div');
    widgetContainer.className = 'tradingview-widget-container__widget';
    widgetContainer.style.height = `${height - 50}px`;
    widgetContainer.style.width = '100%';

    // Copyright container removed for cleaner interface

    containerRef.current.appendChild(widgetContainer);

    // Create and configure the script
    const script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = 'https://s3.tradingview.com/external-embedding/embed-widget-advanced-chart.js';
    script.async = true;

    const config = {
      autosize: true,
      symbol: tradingViewSymbol,
      interval: 'D',
      timezone: 'America/New_York',
      theme: 'light',
      style: '1',
      locale: 'en',
      toolbar_bg: '#f1f3f6',
      enable_publishing: false,
      allow_symbol_change: false,
      calendar: false,
      hide_side_toolbar: false,
      hide_top_toolbar: false,
      hide_legend: false,
      save_image: false,
      container_id: 'tradingview_widget',
      studies: [
        'Volume@tv-basicstudies'
      ],
      show_popup_button: false,
      popup_width: '1000',
      popup_height: '650',
      support_host: 'https://www.tradingview.com'
    };

    script.innerHTML = JSON.stringify(config);

    script.onload = () => {
      setIsLoaded(true);
    };

    script.onerror = () => {
      console.error('Failed to load TradingView widget');
      // Fallback message
      if (containerRef.current) {
        containerRef.current.innerHTML = `
          <div class="flex items-center justify-center h-full bg-muted/30 rounded-lg border border-dashed">
            <div class="text-center p-6">
              <div class="text-lg font-medium text-muted-foreground mb-2">Loading...</div>
              <div class="text-sm text-muted-foreground">TradingView chart for ${symbol}</div>
            </div>
          </div>
        `;
      }
    };

    widgetContainer.appendChild(script);

    // Cleanup function
    return () => {
      if (containerRef.current) {
        containerRef.current.innerHTML = '';
      }
      setIsLoaded(false);
    };
  }, [symbol, height]);

  return (
    <div 
      style={{ width: '100%', height: `${height}px` }}
      className="tradingview-widget-container"
    >
      {/* Loading state */}
      {!isLoaded && (
        <div className="flex items-center justify-center h-full bg-muted/10 rounded-lg border border-dashed animate-pulse">
          <div className="text-center p-6">
            <div className="text-lg font-medium text-muted-foreground mb-2">Loading...</div>
            <div className="text-sm text-muted-foreground">Professional TradingView chart for {symbol}</div>
          </div>
        </div>
      )}

      <div 
        ref={containerRef}
        className={`w-full h-full ${!isLoaded ? 'hidden' : ''}`}
        data-testid={`tradingview-chart-${symbol}`}
      />
    </div>
  );
}

client/src/components/stock-detail-modal.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { TrendingUp, TrendingDown, DollarSign, BarChart3, Brain, Newspaper, MessageSquare, Loader2, Expand } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { stockApi } from "@/lib/api";
import { StockChart } from "./stock-chart";
import StockAIAnalysis from "./stock-ai-analysis";
import StockNews from "./stock-news";
import AIExpertChat from "./ai-expert-chat";
import type { Stock } from "@/types/stock";

interface StockDetailModalProps {
  stock: Stock | null;
  isOpen: boolean;
  onClose: () => void;
}

export function StockDetailModal({ stock, isOpen, onClose }: StockDetailModalProps) {
  if (!stock) return null;

  const [isFullScreenChart, setIsFullScreenChart] = useState(false);
  const [windowSize, setWindowSize] = useState({ width: 1200, height: 800 });
  const isPositive = stock.change >= 0;

  useEffect(() => {
    if (typeof window !== 'undefined') {
      const updateWindowSize = () => {
        setWindowSize({
          width: window.innerWidth,
          height: window.innerHeight
        });
      };

      updateWindowSize();
      window.addEventListener('resize', updateWindowSize);

      return () => window.removeEventListener('resize', updateWindowSize);
    }
  }, []);

  // Fetch detailed stock data including financial metrics
  const { data: detailedData, isLoading: isLoadingDetails } = useQuery({
    queryKey: ["/api/stocks", stock.symbol, "details"],
    queryFn: () => stockApi.getStockDetails(stock.symbol),
    staleTime: 60000, // 1 minute
    enabled: isOpen, // Only fetch when modal is open
  });

  const formatMarketCap = (marketCap: number | null | undefined): string => {
    if (!marketCap) return 'N/A';
    if (marketCap >= 1e12) {
      return `$${(marketCap / 1e12).toFixed(2)}T`;
    } else if (marketCap >= 1e9) {
      return `$${(marketCap / 1e9).toFixed(2)}B`;
    } else if (marketCap >= 1e6) {
      return `$${(marketCap / 1e6).toFixed(2)}M`;
    }
    return `$${marketCap.toLocaleString()}`;
  };

  const formatVolume = (volume: number): string => {
    if (volume >= 1000000) {
      return `${(volume / 1000000).toFixed(1)}M`;
    } else if (volume >= 1000) {
      return `${(volume / 1000).toFixed(1)}K`;
    }
    return volume.toString();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="stock-detail-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 rounded-lg bg-primary/10 flex items-center justify-center">
                <span className="text-primary font-bold text-lg">{stock.symbol}</span>
              </div>
              <div>
                <h2 className="text-2xl font-bold" data-testid={`modal-stock-name-${stock.symbol}`}>
                  {stock.name}
                </h2>
                <p className="text-muted-foreground">{stock.sector || 'Unknown Sector'}</p>
              </div>
            </div>
          </DialogTitle>
        </DialogHeader>

        <div className="mt-6">
          {/* Price Overview */}
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <DollarSign className="w-5 h-5" />
                <span>Price Overview</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center p-4 bg-muted/50 rounded-lg">
                  <div className="text-3xl font-bold" data-testid={`modal-current-price-${stock.symbol}`}>
                    ${stock.currentPrice.toFixed(2)}
                  </div>
                  <div className="text-sm text-muted-foreground">Current Price</div>
                </div>
                <div className="text-center p-4 bg-muted/50 rounded-lg">
                  <div className={`text-2xl font-bold flex items-center justify-center space-x-1 ${
                    isPositive ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'
                  }`}>
                    {isPositive ? <TrendingUp className="w-5 h-5" /> : <TrendingDown className="w-5 h-5" />}
                    <span data-testid={`modal-price-change-${stock.symbol}`}>
                      {isPositive ? '+' : ''}${stock.change.toFixed(2)}
                    </span>
                  </div>
                  <div className="text-sm text-muted-foreground">Change</div>
                </div>
                <div className="text-center p-4 bg-muted/50 rounded-lg">
                  <div className={`text-2xl font-bold ${
                    isPositive ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'
                  }`} data-testid={`modal-change-percent-${stock.symbol}`}>
                    {isPositive ? '+' : ''}{stock.changePercent.toFixed(2)}%
                  </div>
                  <div className="text-sm text-muted-foreground">Change %</div>
                </div>
                <div className="text-center p-4 bg-muted/50 rounded-lg">
                  <div className="text-2xl font-bold" data-testid={`modal-volume-${stock.symbol}`}>
                    {formatVolume(stock.volume)}
                  </div>
                  <div className="text-sm text-muted-foreground">Volume</div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Detailed Information Tabs */}
          <Tabs defaultValue="overview" className="w-full">
            <TabsList className="grid w-full grid-cols-5">
              <TabsTrigger value="overview" className="flex items-center space-x-1">
                <BarChart3 className="w-4 h-4" />
                <span>Overview</span>
              </TabsTrigger>
              <TabsTrigger value="chart">Chart</TabsTrigger>
              <TabsTrigger value="ai-analysis">AI Analysis</TabsTrigger>
              <TabsTrigger value="news">News</TabsTrigger>
              <TabsTrigger value="chat">TradeGPT</TabsTrigger>
            </TabsList>

            <TabsContent value="overview" className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <span>Key Metrics</span>
                      {isLoadingDetails && <Loader2 className="w-4 h-4 animate-spin" />}
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Market Cap</span>
                      <span className="font-medium" data-testid={`modal-market-cap-${stock.symbol}`}>
                        {formatMarketCap(detailedData?.marketCap || stock.marketCap)}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Day High</span>
                      <span className="font-medium" data-testid={`modal-day-high-${stock.symbol}`}>
                        {detailedData?.dayHigh ? `$${detailedData.dayHigh.toFixed(2)}` : 'N/A'}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Day Low</span>
                      <span className="font-medium" data-testid={`modal-day-low-${stock.symbol}`}>
                        {detailedData?.dayLow ? `$${detailedData.dayLow.toFixed(2)}` : 'N/A'}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">52W High</span>
                      <span className="font-medium" data-testid={`modal-52w-high-${stock.symbol}`}>
                        {detailedData?.fiftyTwoWeekHigh ? `$${detailedData.fiftyTwoWeekHigh.toFixed(2)}` : 'N/A'}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">52W Low</span>
                      <span className="font-medium" data-testid={`modal-52w-low-${stock.symbol}`}>
                        {detailedData?.fiftyTwoWeekLow ? `$${detailedData.fiftyTwoWeekLow.toFixed(2)}` : 'N/A'}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">P/E Ratio</span>
                      <span className="font-medium" data-testid={`modal-pe-ratio-${stock.symbol}`}>
                        {detailedData?.peRatio ? detailedData.peRatio.toFixed(2) : 'N/A'}
                      </span>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Company Information</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Sector</span>
                      <Badge variant="secondary">{stock.sector || 'Unknown'}</Badge>
